Interactive
===========
*/

/*
Add the correct display in Chrome and Safari.
*/

summary {
  display: list-item;
}

sources.sass/layout/_slider.scss:
@import url(https://fonts.googleapis.com/css?family=Roboto:400,500);
@import url(https://maxcdn.bootstrapcdn.com/font-awesome/4.5.0/css/font-awesome.min.css);
*, *:before, *:after {
  box-sizing: border-box;
}

html, body {
  height: 100%;
}

body {
  color: #444;
  font-family: 'Roboto', sans-serif;
  font-size: 1rem;
  line-height: 1.5;
}

.slider {
  // height: 100%; 
  position: relative;
  overflow: hidden;
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-flex-flow: row nowrap;
      -ms-flex-flow: row nowrap;
          flex-flow: row nowrap;
  -webkit-box-align: end;
  -webkit-align-items: flex-end;
      -ms-flex-align: end;
          align-items: flex-end;
  -webkit-box-pack: center;
  -webkit-justify-content: center;
      -ms-flex-pack: center;
          justify-content: center;
}
.slider__nav {
  width: 12px;
  height: 12px;
  margin: 15px 12px;

  border-radius: 50%;
  z-index: 2;
  fill: #D9D9D9;
  outline: 6px solid #D9D9D9;
  outline-offset: -6px;
  box-shadow: 0 0 0 0 #D9D9D9,
  0 0 0 0 rgba(51, 51, 51, 0);
  cursor: pointer;
  -webkit-appearance: none;
    -moz-appearance: none;
          appearance: none;
  -webkit-backface-visibility: hidden;
          backface-visibility: hidden;
}
.slider__nav:checked {
  -webkit-animation: check 0.4s linear forwards;
          animation: check 0.4s linear forwards;
}
.slider__nav:checked:nth-of-type(1) ~ .slider__inner {
  left: -16.4%;
}
.slider__nav:checked:nth-of-type(2) ~ .slider__inner {
  left: -150%;
}
.slider__nav:checked:nth-of-type(3) ~ .slider__inner {
  left: -283.2%;
}
.slider__inner {
  position: absolute;
  top: 0;
  left: 0;
  width: 400%;
  height: 100%;
  -webkit-transition: left 0.4s;
  transition: left 0.4s;
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-flex-flow: row nowrap;
      -ms-flex-flow: row nowrap;
          flex-flow: row nowrap;
}

.slider__contents {
  height: 100%;
  padding: 2rem;
  text-align: center;
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-flex: 1;
  -webkit-flex: 1;
      -ms-flex: 1;
          flex: 1;
  -webkit-flex-flow: column nowrap;
      -ms-flex-flow: column nowrap;
          flex-flow: column nowrap;
  -webkit-box-align: center;
  -webkit-align-items: center;
      -ms-flex-align: center;
          align-items: center;
  -webkit-box-pack: center;
  -webkit-justify-content: center;
      -ms-flex-pack: center;
          justify-content: center;
}
.slider__image {
  font-size: 2.7rem;
      color: #2196F3;
}
.slider__caption {
  font-weight: 500;
  margin: 2rem 0 1rem;
  text-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
  text-transform: uppercase;
}
.slider__txt {
  color: #999;
  margin-bottom: 3rem;
  max-width: 300px;
}

@-webkit-keyframes check {
  50% {
    outline-color: #333;
    box-shadow: 0 0 0 12px #333, 0 0 0 36px rgba(51, 51, 51, 0.2);
  }
  100% {
    outline-color: #333;
    box-shadow: 0 0 0 0 #333, 0 0 0 0 rgba(51, 51, 51, 0);
  }
}

@keyframes check {
  50% {
    fill: #D31342;
    outline-color: #D31342;
    box-shadow: 0 0 0 12px #D31342, 0 0 0 36px rgba(51, 51, 51, 0.2);
  }

  100% {
    fill: #D31342;
    outline-color: #D31342;
    box-shadow: 0 0 0 0 #D31342, 0 0 0 0 rgba(51, 51, 51, 0);
  }
}

sources.sass/base/_common.scss:
body {
  font-family: 'DM Sans', sans-serif !important;
}
img {
  display: block;
  max-width: 100%;
}
button {
  border: 0;
  cursor: pointer;
}
.visually-hidden {
  position: absolute;
  white-space: nowrap;
  width: 1px;
  height: 1px;
  overflow: hidden;
  border: 0;
  padding: 0;
  clip: rect(0 0 0 0);
  clip-path: inset(50%);
  margin: -1px;
}
html {
  scroll-behavior: smooth;
}

sources.sass/components/_container.scss:
.container {
  padding-left: 15px;
  padding-right: 15px;
  margin: 0 auto;

  @include tel {
    width: 320px;
  }

  @include tab {
    width: 734px;
  }

  @include desk {
    width: 1275px;
  }
}

sources.sass/utils/_mixins.scss:
@mixin tel {
    @media screen and (min-width: 320px) {
        @content;
    }
}

@mixin tab {
    @media screen and (min-width: 734px) {
        @content;
    }
}

@mixin desk {
    @media screen and (min-width: 1275px) {
        @content;
    }
}

@mixin font($fs, $fw, $lh, $ls) {
    font-size: $fs;
    font-weight: $fw;
    line-height: $lh;
    letter-spacing: $ls;
}

sources.sass/components/_scroll-up.scss:
.back-to-top {
  display: none;
  align-items: center;
  justify-content: center;
  position: fixed;
  right: 30px;
  bottom: 30px;
  width: 50px;
  height: 50px;
  background-color: $second-color-text;
  border-radius: 50%;
  cursor: pointer;
  z-index: 9999;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
  transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1);

  opacity: 0.4;
  &:hover,
  &:focus {
    opacity: 0.9;
  }
}
.back-to-top__arrow {
  width: 30px;
  height: 30px;

  fill: $primary-color-text;
}
.show {
  display: flex;
}

sources.sass/components/_modal.scss:
.backdrop {
    position: fixed;
    z-index: 99;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0.2);

    transition: opacity 250ms, visibility 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

.is-hidden {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
}

.modal {
    position: absolute;
    z-index: 999;
    top: 50%;
    left: 50%;
    width: 320px;
    padding: 60px 30px;
    transform: translate(-50%, -50%) scale(1);
    background-color: $background-color-4;
    background-image: url(../images/img/products/icecream-png-2.jpg);
    background-repeat: no-repeat;
    background-size: contain;
    background-position: right;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
        0px 2px 1px rgba(0, 0, 0, 0.2);
    border-radius: 24px;

    transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);

    .backdrop.is-hidden & {
        transform: translate(-50%, -50%) scale(0.8);
    }

    @include tab {
        width: 500px;
        padding: 40px;
    }

    @include desk {
        width: 600px;
    }
}

.close-btn {
    position: absolute;
    top: 10px;
    right: 10px;

    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;

    background: $white-color;
    border-radius: 50%;
    transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1), background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

    &:hover,
    &:focus {
        background-color: #ecebeb;
        box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);
    }
}

.close-btn__icon {
    fill: $primary-color-text;
}

.modal__title {
    @include font(24px, 400, 1.15, 0.04em);
    font-family: 'Titan One';
    text-align: start;
    text-transform: uppercase;
    color: $primary-color-text;
    margin-bottom: 20px;
    @include tab {
        font-size: 32px;
    }
}

.modal-text {
    @include font(18px, 400, 1.17, 0.04em);
    font-family: 'Titan One';
    text-align: start;
    text-transform: uppercase;
    color: $second-color-text;

    @include tab {
        font-size: 22;
    }
}

.modal-form__field {
    margin-bottom: 20px;
}

.modal-form__label {
    display: block;
    @include font(14px, 500, 1.66, 0.04em);
    font-family: 'DM Sans';
    color: $second-color-text;

    transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);

    .modal-form__input:hover &,
    .modal-form__input:focus & {
        color: $primary-color-text;
    }
}

.modal-form__input-wrap {
    position: relative;
}

.modal-form__input {
    width: 100%;
    height: 40px;
    border-top-style: none;
    border-left-style: none;
    border-right-style: none;
    border-width: 1px;
    border-color: $second-color-text;
    outline: none;
    padding-left: 10px;

    transition: border-bottom 250ms cubic-bezier(0.4, 0, 0.2, 1);

    &:hover,
    &:focus {
        border-bottom: 1px solid $primary-color-text;
    }
}

.submit-btn {
display: inline-block;
padding: 7px 14px;
@include font(14px, 400, 1.25, 0.04em);
font-family: 'DM Sans';
text-align: center;
color: $white-color;
border-radius: 20px;
background-color: rgba(212, 20, 67, 1);
margin-bottom: 20px;

transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1), box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1);

&:hover,
&:focus {
    background-color: rgba(199, 19, 62, 1);
    box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);
}
}

sources.sass/layout/_header.scss:
.header {
  background-color: $background-color-1;
}

.header-container {
  padding-top: 8px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  @include tab {
    padding-top: 32px;
  }
  @include desk {
    padding-top: 37px;
  }
}
.menu-open-btn {
  position: relative;
  margin-left: auto;
  background-color: inherit;
  & .menu-icon__close {
    display: block;
    position: fixed;
    z-index: 111;
    fill: $white-color;
    top: 20px;
    right: 20px;
    animation: animate-menu 1500ms 1 0ms;
    @include tab {
      right: 30px;
      top: 30px;
    }
  }

  & .menu-icon__open {
    display: none;
  }

  &.is-open .menu-icon__close {
    display: none;
  }

  &.is-open .menu-icon__open {
    display: block;
  }
  @include desk {
    display: none;
  }
}
.container-menu {
  display: none;
  position: fixed;
  overflow: hidden;
  top: 0;
  right: 0;
  margin-left: 35px;
  margin-right: 0px;
  width: 250px;
  height: 100vh;
  background-color: #e17992;
  text-align: start;
  font-size: 20px;
  z-index: 10;
  box-shadow: 5px 5px 5px 10px #e17992;
  animation: animate-menu 1500ms 1 0ms;
  @include tab {
    margin-left: 240px;
    margin-right: 0;
    width: 258px;

  }
@media screen and (max-width:1275px){
  &.is-open {
    height: 100vh;
    display: flex;
    flex-direction: column;
  }
}

  @keyframes animate-menu {
    0% {
      transform: translateX(450px);
    }

    100% {
      transform: translateX(0);
    }
  }
}

.site-nav__mobile-list {
  height: 42px;
  border-top: 1px solid #e18298;
  padding-left: 20px;
  padding-top: 13px;
}
.site-nav__mobile-link {
  font-family: 'DM Sans';
  font-style: normal;
  font-weight: 700;
  font-size: 14px;
  line-height: 18px;
  color: $white-color;
  &:hover,
  &:focus {
    color: $primary-color-text;
  }
}

.site-nav {
  display: none;
  @include desk {
    display: flex;
    align-items: center;
  }
}

.site-nav__items {
  z-index: 10;
}
.site-nav__items:not(:last-child) {
  margin-right: 35px;
}
.site-nav__link {
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.31px;
  color: $white-color;
  transition: color 300ms cubic-bezier(0.4, 0, 0.2, 1);
  &:hover,
  &:focus {
    color: $primary-color-text;
  }
}
.header-buy__button-menu {
  display: block;
  width: 209px;
  height: 44px;
  border-radius: 22px;
  background-color: $white-color;
  margin-left: auto;
  margin-right: auto;
  margin-top: 58px;
  margin-bottom: 19px;

  font-family: 'DM Sans';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.31;
  text-align: center;
  color: $primary-color-text;
  transition: background-color 300ms cubic-bezier(0.4, 0, 0.2, 1);

  @include tab {
    width: 184px;
    height: 44px;
  }
  &:hover,
  &:focus {
    background-color: #ecebeb;
  }
}

.header-buy__button {
  display: none;
  @include tab {
    display: block;
    width: 160px;
    height: 44px;
    border-radius: 22px;
    background-color: $white-color;
    margin-left: 32px;

    font-family: 'DM Sans';
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 1.31;
    text-align: center;
    color: $primary-color-text;
    transition: background-color 300ms cubic-bezier(0.4, 0, 0.2, 1);

    &:hover,
    &:focus {
      background-color: #ecebeb;
    }
  }
}

sources.sass/layout/_hero.scss:
