.contacts-btn-location {
  width: 161px;
  height: 44px;
  border-radius: 22px;
  background-color: $primary-color-text;
  color: $white-color;
  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    background-color: #ee0840;
    box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);
    transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1);
    outline: none;
  }
  @include tab {
    width: 192px;
  }

  @include desk {
    width: 198px;
  }
}

.contacts-btn-franchise {
  width: 161px;
  height: 44px;
  border-radius: 22px;
  background-color: $white-color;
  color: $primary-color-text;
  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    background-color: #ecebeb;
    box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);
    transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1);
    outline: none;
  }
  @include tab {
    width: 174px;
  }

  @include desk {
    width: 180px;
  }
}

.contacts-btn-name {
  font-family: DM Sans;
  font-size: 16px;
  font-weight: 700;
  line-height: 1.31;
  letter-spacing: 0em;
  text-align: center;

  margin-right: 8px;
}

.contacts-list-btn:nth-child(1) {
  @media screen and (max-width: 733px) {
    margin-bottom: 10px;
  }

  @include tab {
    margin-right: 14px;
  }

  @include desk {
    margin-right: 15px;
  }
}

sources.sass/layout/_footer.scss:
.footer {
    background-color: #FFF5F6;
    @include tel {
        padding-top: 24px;
    }
    @include tab {
        padding-top: 20px;
    }
    @include desk {
        padding-top: 60px;
    }
}
.footer--container{
    @include tab {
        display: flex;
        justify-content: space-between;

    }
    @include desk {
        align-items: center;
    }
}
.footer__logo{
    // display: flex;
    // flex-wrap: wrap;
    // flex-direction: column;

    font-family: 'Titan One';
    font-style: normal;
    font-weight: 400;
    font-size: 18px;
    line-height: 1.16;
    text-transform: uppercase;
    @include tab {
        font-size: 26px;
        line-height: 1.15;
        letter-spacing: 0.04em;
    }
    @include desk {
    font-size: 30px;
    line-height: 1.13;
    }
    color: $second-color-text;

    @media screen and (max-width: 1274px) {
        width: 234px;
    }
}
.footer__logo-top {
font-family: 'Titan One';
font-size: 18px;
line-height: 21px;
text-transform: uppercase;
color: #FFA5BA;
width: 232px;
@media screen and (min-width: 1275px) {
    width: 472px;
    font-size: 30px;
    line-height: 34px;
    letter-spacing: 0.04em;
    text-transform: uppercase;
}
@media screen and (min-width: 734px) and (max-width: 1274px) {
    width: 340px;
    font-size: 26px;
    line-height: 30px;
    letter-spacing: 0.04em;
    text-transform: uppercase;
}
}
.footer__logo-bottom{
    font-family: 'Titan One';
    margin-top: 16px;
    font-size: 34px;
    line-height: 1.14;
    letter-spacing: 0.04em;
    text-transform: uppercase;
    @include tab {
        font-size: 48px;
        line-height: 1.14;
        letter-spacing: 0.06em;
    }
    @include desk {
        font-size: 58px;
        line-height: 1.13;
    }
    color: $primary-color-text;
}

.social__list {
    @media screen and (max-width: 733px) {
    margin-top: 40px;
    }

    display: flex;
    justify-content: flex-end;
}
.social__item:not(:last-child) {
    margin-right: 28.24px;
}
.social__link {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0;
    border: 0;
    width: 32px;
    height: 32px;
    border-radius: 50%;
}
.social__link--footer {
    height: 32px;
    width: 32px;
    background-color: $second-color-text;
    color: #ffffff;
    &:hover,
    &:focus {
        background-color: $primary-color-text;
    }
    transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1),
    background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  }

.social__icon {
    fill: currentColor;
    height: 20px;
    width: 20px;
}
.footer__text {
    margin-top: 40px;
    font-family: DM Sans;
    font-style: normal;
    font-weight: bold;
    font-size: 12px;
    line-height: 186%;
    @include tab {
        font-size: 14px;
    }
    @include desk {
        font-size: 16px;
        letter-spacing: 0.02em;
    }
    text-align: right;
    color: #000000;
}
.footer__tel {
    color: $primary-color-text;
}

.licence{
    font-family: 'DM Sans';
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    line-height: 152%;
    text-align: center;

    color: #907E82;

  @media screen and (max-width: 733px) {
    text-align: -webkit-center;
        }
    @include tab {
        line-height: 186%;
        letter-spacing: 0.02em;


    }
    margin-top: 40px;
    padding-top: 16px;
    padding-bottom: 20px;
    @include tab {
        padding-top: 8px;
        padding-bottom: 8px;
    }

    border-top: 1px solid #E1E1E1;

}
.licence__text {
    @media screen and (max-width: 733px) {
        width: 149px;
    }
}

sources.sass/layout/_products.scss:
.products {
    background-color:$white-color;
    text-align: center;
    padding-top: 80px;
    padding-bottom: 40px;
    @include tab {
        padding-top: 40px;
        padding-bottom: 60px;
    }
@include desk {
    padding-top: 60px;
    padding-bottom: 60px;
}
}

.section-title {
    @include font(34px, 400, 1.15, 0.04em);
font-family: 'Titan One';
text-align: center;
text-transform: uppercase;
color: $primary-color-text;
margin-bottom: 148px;
@include tab {
    font-size: 48px;
    margin-bottom: 154px;
}
@include desk {
    font-size: 58px;
    line-height: 1.14;
    letter-spacing: 0.06em;
    margin-bottom: 258px;
}
}

.section-title__subtitle {
    @include font(18px, 400, 1.17, 0.04em);
    font-family: 'Titan One';
    text-align: center;
    text-transform: uppercase;
    color: $second-color-text;
    margin-bottom: 15px;
    @include tab {
        font-size: 26px;
        line-height: 1.15;
    }
    @include desk {
        font-size: 30px;
        line-height: 1.13;
    }
}

.title {
    margin-bottom: 70px;
    &::after {
        content: '';
        display: block;
        position: absolute;
        width: 26px;
        height: 6px;
        fill: $white-color;
        left: 130px;
        top: 58px;
        background-image: url(../images/svg/dots.svg);
        background-repeat: no-repeat;
        background-size: cover;
        background-position: center;
    }
    @include font(20px, 400, 1.15, 0.04em);
    font-family: 'Titan One';
    text-align: center;
    text-transform: uppercase;
    color: $white-color;
    @include desk {
        font-size: 30px;
        line-height: 1.13;
    }
}

@include tab {
    .title::after {
    left: 98px;
    top: 58px;
    }
}

@include desk {
    .title::after {
        left: 180px;
        top: 70px;
    }
}

.section-text {
    @include font(14px, 700, 1.52, 0.04em);
    font-family: 'DM Sans';
    text-align: center;
    color: $white-color;
@media screen and (min-width: 734px) {
    line-height: 1.76;
}
@media screen and (min-width: 1275px) {
    font-size: 16px;
    line-height: 1.76;
}
}

.products-list {
    @media screen and (min-width: 734px) {
        display: flex;
        justify-content: space-between;
    }
}

.products-list__item {
width: 290px;
height: 489px;
border-radius: 24px;
padding-top: 160px;
padding-bottom: 40px;
margin-bottom: 140px;
padding-top: 0;
&:last-child {
    margin-bottom: 0;
}
&::before {
    content: '';
    display: block;
    height: 280px;
    transform: translateY(-140px);
    margin-bottom: 20px;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
}
@media screen and (min-width: 734px) and (max-width: 1274px) {
    width: 224px;
    height: 473px;
    margin-bottom: 0;
    &::before {
        height: 224px;
        margin-bottom: 32px;
        transform: translateY(-138px);
    }
}
@media screen and (min-width: 1275px) {
    width: 394px;
    height: 535px;
    padding-bottom: 60px;
    margin-bottom: 0;
    &::before {
        height: 394px;
        margin-bottom: 32px;
        transform: translateY(-258px);
    }
}
}

.products-list__item-box {
    transform: translateY(-140px);
    padding: 0 40px;
    @include tab {
        padding: 0 32px;
        transform: translateY(-138px);
    }
    @include desk {
        padding: 0 75px;
        transform: translateY(-258px);
    }
}

.first-product {
background-color: $background-color-1;
&::before {
    background-image: url(../images/img/products/icecream-mob.png);
    @media (min-device-pixel-ratio: 2),
    (min-resolution: 192dpi),
    (min-resolution: 2dppx) {
        background-image: url(../images/img/products/icecream-mob-2x.png);
    }
}
}
@include tab {
    .first-product::before {
        background-image: url(../images/img/products/icecream-tab.png);

        @media (min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/img/products/icecream-tab-2x.png);
        }
    }
}
@include desk {
    .first-product::before {
        background-image: url(../images/img/products/ice-cream.png);

        @media (min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/img/products/ice-cream-2x.png);
        }
    }
}

.second-product {
    background-color: $background-color-2;
    &::before {
        background-image: url(../images/img/products/icecoffee-mob.png);
        @media (min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/img/products/icecoffee-mob-2x.png);
        }
    }    
}
@include tab {
    .second-product::before {
        background-image: url(../images/img/products/icecoffee-tab.png);

        @media (min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/img/products/icecoffee-tab-2x.png);
        }
    }
}

@include desk {
    .second-product::before {
        background-image: url(../images/img/products/icecoffee.png);

        @media (min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/img/products/icecoffee-2x.png);
        }
    }
}

.third-product {
    background-color: $background-color-3;
    &::before {
        background-image: url(../images/img/products/milkshake-mob.png);
        @media (min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/img/products/milkshake-mob-2x.png);
        }
    }
}
@include tab {
    .third-product::before {
        background-image: url(../images/img/products/milkshake-tab.png);

        @media (min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/img/products/milkshake-tab-2x.png);
        }
    }
}

@include desk {
    .third-product::before {
        background-image: url(../images/img/products/milkshake.png);

        @media (min-device-pixel-ratio: 2),
        (min-resolution: 192dpi),
        (min-resolution: 2dppx) {
            background-image: url(../images/img/products/milkshake-2x.png);
        }
    }
}

.desc-container {
    height: 124px;
margin-bottom: 32px;
overflow: hidden;
@include tab {
    height: 150px;
}
@include desk {
    height: 106px;
    margin-bottom: 60px;
}
}

.products-btn {
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    width: 40px;
    height: 40px;
    background-color: $white-color;
    transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1),
    background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

    &:hover,
    &:focus {
        background-color: #ecebeb;
        box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);
    }
}

.products-btn__icon {
    fill: $primary-color-text;
}
